import{_ as t}from"./chunks/v4_displays_add_sync_groups.DtJC9nrB.js";import{_ as a,c as l,o as n,ae as o}from"./chunks/framework.CYV0OCM_.js";const h=JSON.parse('{"title":"동기화 그룹","description":"동기화 그룹은 동기화된 콘텐츠를 표시할 디스플레이를 포함합니다","frontmatter":{"title":"동기화 그룹","description":"동기화 그룹은 동기화된 콘텐츠를 표시할 디스플레이를 포함합니다"},"headers":[],"relativePath":"displays/sync-groups.md","filePath":"displays/sync-groups.md"}'),i={name:"displays/sync-groups.md"};function e(r,s,p,c,u,d){return n(),l("div",null,s[0]||(s[0]=[o('<h1 id="동기화-그룹" tabindex="-1">동기화 그룹 <a class="header-anchor" href="#동기화-그룹" aria-label="Permalink to &quot;동기화 그룹&quot;">​</a></h1><p>동기화 그룹은 동기화된 콘텐츠를 표시할 디스플레이를 포함합니다. <a href="/SignaLink-manual/scheduling/events.html#synchronised-events">동기화된 이벤트</a>를 예약할 때 콘텐츠를 미러링 또는 비디오 월 구성에서 2개 이상의 디스플레이에서 동기화하여 재생할 수 있습니다.</p><p>::: feat 동기화 그룹|v4 :::</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><strong>콘텐츠 동기화 키</strong>를 사용하여 다른 레이아웃에서 <a href="/SignaLink-manual/layouts/editor/playlists.html">재생 목록</a>을 동기화하세요.</p></div><p><a href="/SignaLink-manual/displays.html">디스플레이</a>를 먼저 <strong>동기화 그룹</strong>에 할당해야 합니다:</p><ul><li>CMS 메인 메뉴의 <strong>디스플레이</strong> 섹션에서 <strong>동기화 그룹</strong>을 클릭하세요.</li><li><strong>동기화 그룹 추가</strong> 버튼을 선택하세요.</li></ul><p><img src="'+t+'" alt="동기화 그룹 추가"></p><p>동기화 그룹은 <a href="/SignaLink-manual/tour/folders.html">폴더</a>에 저장하여 디스플레이에 대한 사용자/사용자 그룹 상호작용 수준을 쉽게 제어하고 추가적인 구성 방법을 제공할 수 있습니다.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>동기화 그룹의 모든 디스플레이는 지정된 게시자 포트에서 TCP를 통해 LAN IP 주소를 사용하여 통신해야 합니다. 기본값은 9590이지만 해당 포트가 예약된 경우 변경할 수 있습니다.</p><p>최상의 동기화를 위해 유선 네트워크와 고성능 유사 디바이스를 사용하는 것을 권장합니다.</p></div><ul><li>저장 시 <strong>멤버십 관리</strong> 폼을 사용하여 이 그룹에 포함할 디스플레이를 선택하세요.</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p><strong>참고:</strong> 디스플레이는 동시에 하나의 동기화 그룹에만 속할 수 있습니다.</p></div><ul><li><p>저장 시 편집 폼을 사용하여 드롭다운에서 동기화 그룹의 <strong>리드 디스플레이</strong>가 될 디스플레이를 선택하세요.</p></li><li><p><strong>저장</strong>을 클릭하세요</p></li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>이제 CMS 메인 메뉴에서 <strong>일정</strong>을 클릭하고 <a href="/SignaLink-manual/scheduling/events.html#synchronised-events">동기화된 이벤트 추가 버튼</a>을 선택하여 동기화 그룹에 콘텐츠를 예약할 수 있습니다.</p></div><h2 id="편집-멤버십-관리" tabindex="-1">편집 / 멤버십 관리 <a class="header-anchor" href="#편집-멤버십-관리" aria-label="Permalink to &quot;편집 / 멤버십 관리&quot;">​</a></h2><p>행 메뉴를 사용하여 동기화 그룹을 관리하세요:</p><ul><li><strong>편집</strong>을 선택하여 대체 리드 디스플레이를 설정하세요</li><li><strong>멤버</strong>를 선택하여 어떤 디스플레이가 그룹의 멤버인지 관리하세요.</li></ul><h4 id="다음" tabindex="-1">다음... <a class="header-anchor" href="#다음" aria-label="Permalink to &quot;다음...&quot;">​</a></h4><p><a href="/SignaLink-manual/scheduling/events.html#synchronised-events">동기화된 이벤트 예약</a></p>',18)]))}const _=a(i,[["render",e]]);export{h as __pageData,_ as default};
