import{_ as n}from"./chunks/v4_displays_add_sync_groups.DtJC9nrB.js";import{_ as s,c as t,o,ae as i}from"./chunks/framework.CYV0OCM_.js";const m=JSON.parse('{"title":"Sync Groups","description":"Sync Groups contain Displays to show synchronised content","frontmatter":{"title":"Sync Groups","description":"Sync Groups contain Displays to show synchronised content"},"headers":[],"relativePath":"en/displays/sync-groups.md","filePath":"en/displays/sync-groups.md"}'),a={name:"en/displays/sync-groups.md"};function r(l,e,c,p,d,u){return o(),t("div",null,e[0]||(e[0]=[i('<h1 id="sync-groups" tabindex="-1">Sync Groups <a class="header-anchor" href="#sync-groups" aria-label="Permalink to &quot;Sync Groups&quot;">​</a></h1><p>Sync Groups contain the Displays that will show synchronised content. Content can be synchronised to play across 2 or more Displays in a mirrored or video wall configuration when scheduling a <a href="/SignaLink-manual/scheduling/events.html#synchronised-events">Synchronised Event</a>.</p><p>::: feat Sync Groups|v4 :::</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Sync <a href="/SignaLink-manual/guide/layouts/editor/playlists.html">Playlists</a> on different Layouts using the <strong>Content Synchronisation Key</strong>.</p></div><p><a href="/SignaLink-manual/guide/displays.html">Displays</a> first need to be assigned to a <strong>Sync Group</strong>:</p><ul><li>Click on <strong>Sync Groups</strong> under the <strong>Displays</strong> section of the main CMS menu.</li><li>Select the <strong>Add Sync Group</strong> button.</li></ul><p><img src="'+n+'" alt="Add Sync Group"></p><p>Sync Groups can be saved to <a href="/SignaLink-manual/guide/tour/folders.html">Folders</a> to easily control User/User Group levels of interaction for Displays as well as provide an additional way to organise.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>All Displays in a Sync Group need to communicate using their LAN IP address over TCP on the publisher port specified. This is defaulted to 9590 but can be changed if that port is reserved.</p><p>We recommend using a wired network and high-powered similar devices for the best Synchronisation.</p></div><ul><li>On saving use the <strong>Manage Membership</strong> form to select which Displays to include in this Group.</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p><strong>NOTE:</strong> A Display can only belong to one Sync Group at the same time.</p></div><ul><li><p>On saving use the Edit form use the drop down to select which Display should be the <strong>Lead Display</strong> for the Sync Group.</p></li><li><p>Click to <strong>Save</strong></p></li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Content can now be scheduled to the Sync Group by clicking <strong>Schedule</strong> from the main CMS menu and selecting the <a href="/SignaLink-manual/guide/scheduling/events.html#synchronised-events">Add Synchronised Event button</a>.</p></div><h2 id="edit-manage-membership" tabindex="-1">Edit / Manage Membership <a class="header-anchor" href="#edit-manage-membership" aria-label="Permalink to &quot;Edit / Manage Membership&quot;">​</a></h2><p>Manage a Sync Group by using the row menu:</p><ul><li>Select <strong>Edit</strong> to set an alternative Lead Display</li><li>Select <strong>Members</strong> to manage which Displays are members of the group.</li></ul><h4 id="next" tabindex="-1">Next... <a class="header-anchor" href="#next" aria-label="Permalink to &quot;Next...&quot;">​</a></h4><p><a href="/SignaLink-manual/guide/scheduling/events.html#synchronised-events">Scheduling Synchronised Events</a></p>',18)]))}const y=s(a,[["render",r]]);export{m as __pageData,y as default};
