import{_ as a,a as i,b as t}from"./chunks/v4_layouts_remote_search.Dz6Nucrl.js";import{_ as o,c as r,o as l,ae as s}from"./chunks/framework.CYV0OCM_.js";const b=JSON.parse('{"title":"Library Search","description":"Use Library Searches to easily add media to Layouts","frontmatter":{"title":"Library Search","description":"Use Library Searches to easily add media to Layouts"},"headers":[],"relativePath":"en/layouts/editor/library-search.md","filePath":"en/layouts/editor/library-search.md"}'),n={name:"en/layouts/editor/library-search.md"};function d(c,e,h,u,p,g){return l(),r("div",null,e[0]||(e[0]=[s('<h1 id="library-search" tabindex="-1">Library Search <a class="header-anchor" href="#library-search" aria-label="Permalink to &quot;Library Search&quot;">​</a></h1><p>Add media which already exists in the <a href="/SignaLink-manual/guide/media/library.html">CMS Library</a> to Layouts/Playlists using the Library Search function.</p><p>Library media consists of the following:</p><ul><li><a href="/SignaLink-manual/guide/widgets/image.html">Images</a></li><li><a href="/SignaLink-manual/guide/widgets/audio.html">Audio</a></li><li><a href="/SignaLink-manual/guide/widgets/video.html">Video</a></li><li>Other media files</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><strong>Other media</strong> search includes <a href="/SignaLink-manual/guide/widgets/flash.html">Flash</a>, <a href="/SignaLink-manual/guide/widgets/html-package.html">HTML Package</a>, <a href="/SignaLink-manual/guide/widgets/pdf.html">PDF</a> and <a href="/SignaLink-manual/guide/widgets/powerpoint.html">PowerPoint</a> files available from the <strong>Type</strong> drop down menu.</p></div><p><img src="'+a+'" alt="Library Search"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Use the available filters to narrow down searches!</p></div><ul><li>Drag or select to highlight and click on the Layout to add.</li><li>Position and resize.</li><li>Configuration options will be shown in the Properties Panel.</li></ul><p>New files of the selected type, can also be added from here:</p><p><img src="'+i+'" alt="Upload file to Layouts"></p><ul><li>Click on the Library search for the media type you wish to add. If you want to add a file using the Other media search, ensure you have selected the file type from the drop down.</li><li>Drag or click <strong>Upload</strong> to the Layout.</li><li>Add files, optionally select a Folder and <strong>upload</strong>.</li><li>On clicking <strong>Done</strong>, the file will be added ready for positioning/resizing and configuration.</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Files that are uploaded directly to Layouts/Playlists will be automatically added to your CMS Library!</p></div><h2 id="remote-search" tabindex="-1">Remote Search <a class="header-anchor" href="#remote-search" aria-label="Permalink to &quot;Remote Search&quot;">​</a></h2><p>Integration from Pixabay gives users additional resources to Image and Video files which can be added to Layouts without having to leave the Layout Editor to source.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Pixabay integration is enabled by default for new SignaLink in the Cloud customers.</p><p>If you are upgrading from an earlier version you will need to enable by navigating to the <strong>Administration</strong> section of the CMS and click <strong>Applications</strong>. Scroll down the page to the <strong>Connectors</strong> section. Click to <strong>Configure</strong> and tick to enable, <strong>Save</strong> changes.</p></div><ul><li>Click on <strong>Pixabay</strong> and select which <strong>Type</strong> of file to search.</li><li>Enter search criteria in the Name field to return results.</li><li>Drag or click to add, position/resize and configure.</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Clicking the arrow in the top right of a thumbnail will play a preview in a separate window. This window can also be enlarged using the arrow in the top left and can be added to the Layout from here by clicking the <code>+</code> button at the top.</p></div><p><img src="'+t+'" alt="Pixabay Search"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Images/videos that are selected from Pixabay to be used will automatically be added to your Library and available for future use from the Image/Video Library Search functions!</p></div><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><p><a href="/SignaLink-manual/guide/layouts/editor/widgets.html">Widgets</a></p>',21)]))}const f=o(n,[["render",d]]);export{b as __pageData,f as default};
